(function(){this.addComment=function(){var n,t;if(t=$("#new-comment-form"),n=t.find("input[type='text']").val(),n!=="")return $.get(""+window.location.href+"&new-comm="+n,function(t){return $(t).find("#comments-list .comment:last").css("display","none").appendTo($("#comments-list")).slideDown("fast"),console.log("done posting comment "+n)})};this.deleteComment=function(){var t,n;return t=$(event.currentTarget).parent(".comment"),n=t.attr("db-id"),$.ajax({url:""+window.location.href+"&del-comm="+n}).done(function(){return t.slideUp("fast"),console.log("done deleting comment with id "+n+" at "+window.location.href+"&del-comm="+n)})}}).call(this);